import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertAssetSchema, insertMaintenanceTaskSchema, insertAlertSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Dashboard metrics
  app.get("/api/dashboard/metrics", async (_req, res) => {
    try {
      const metrics = await storage.getDashboardMetrics();
      res.json(metrics);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch dashboard metrics" });
    }
  });

  // Assets endpoints
  app.get("/api/assets", async (_req, res) => {
    try {
      const assets = await storage.getAssets();
      res.json(assets);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch assets" });
    }
  });

  app.get("/api/assets/:id", async (req, res) => {
    try {
      const asset = await storage.getAsset(req.params.id);
      if (!asset) {
        return res.status(404).json({ message: "Asset not found" });
      }
      res.json(asset);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch asset" });
    }
  });

  app.post("/api/assets", async (req, res) => {
    try {
      const validatedData = insertAssetSchema.parse(req.body);
      const asset = await storage.createAsset(validatedData);
      res.status(201).json(asset);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid asset data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create asset" });
    }
  });

  app.put("/api/assets/:id", async (req, res) => {
    try {
      const validatedData = insertAssetSchema.partial().parse(req.body);
      const asset = await storage.updateAsset(req.params.id, validatedData);
      if (!asset) {
        return res.status(404).json({ message: "Asset not found" });
      }
      res.json(asset);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid asset data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update asset" });
    }
  });

  app.delete("/api/assets/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteAsset(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Asset not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete asset" });
    }
  });

  // Maintenance tasks endpoints
  app.get("/api/maintenance", async (_req, res) => {
    try {
      const tasks = await storage.getMaintenanceTasks();
      res.json(tasks);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch maintenance tasks" });
    }
  });

  app.get("/api/maintenance/asset/:assetId", async (req, res) => {
    try {
      const tasks = await storage.getMaintenanceTasksByAsset(req.params.assetId);
      res.json(tasks);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch maintenance tasks for asset" });
    }
  });

  app.post("/api/maintenance", async (req, res) => {
    try {
      const validatedData = insertMaintenanceTaskSchema.parse(req.body);
      const task = await storage.createMaintenanceTask(validatedData);
      res.status(201).json(task);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid maintenance task data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create maintenance task" });
    }
  });

  app.put("/api/maintenance/:id", async (req, res) => {
    try {
      const validatedData = insertMaintenanceTaskSchema.partial().parse(req.body);
      const task = await storage.updateMaintenanceTask(req.params.id, validatedData);
      if (!task) {
        return res.status(404).json({ message: "Maintenance task not found" });
      }
      res.json(task);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid maintenance task data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update maintenance task" });
    }
  });

  // Alerts endpoints
  app.get("/api/alerts", async (_req, res) => {
    try {
      const alerts = await storage.getAlerts();
      res.json(alerts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch alerts" });
    }
  });

  app.get("/api/alerts/active", async (_req, res) => {
    try {
      const alerts = await storage.getActiveAlerts();
      res.json(alerts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch active alerts" });
    }
  });

  app.post("/api/alerts", async (req, res) => {
    try {
      const validatedData = insertAlertSchema.parse(req.body);
      const alert = await storage.createAlert(validatedData);
      res.status(201).json(alert);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid alert data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create alert" });
    }
  });

  app.put("/api/alerts/:id", async (req, res) => {
    try {
      const validatedData = insertAlertSchema.partial().parse(req.body);
      const alert = await storage.updateAlert(req.params.id, validatedData);
      if (!alert) {
        return res.status(404).json({ message: "Alert not found" });
      }
      res.json(alert);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid alert data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update alert" });
    }
  });

  app.delete("/api/alerts/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteAlert(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Alert not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete alert" });
    }
  });

  // Performance metrics endpoints
  app.get("/api/metrics/:assetId", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 30;
      const metrics = await storage.getPerformanceMetrics(req.params.assetId, limit);
      res.json(metrics);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch performance metrics" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Asset, 
  type InsertAsset,
  type MaintenanceTask,
  type InsertMaintenanceTask,
  type Alert,
  type InsertAlert,
  type PerformanceMetric,
  type InsertPerformanceMetric,
  AssetStatus,
  MaintenanceType,
  Priority,
  AlertLevel
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Users
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Assets
  getAssets(): Promise<Asset[]>;
  getAsset(id: string): Promise<Asset | undefined>;
  getAssetByAssetId(assetId: string): Promise<Asset | undefined>;
  createAsset(asset: InsertAsset): Promise<Asset>;
  updateAsset(id: string, asset: Partial<InsertAsset>): Promise<Asset | undefined>;
  deleteAsset(id: string): Promise<boolean>;

  // Maintenance Tasks
  getMaintenanceTasks(): Promise<MaintenanceTask[]>;
  getMaintenanceTask(id: string): Promise<MaintenanceTask | undefined>;
  getMaintenanceTasksByAsset(assetId: string): Promise<MaintenanceTask[]>;
  createMaintenanceTask(task: InsertMaintenanceTask): Promise<MaintenanceTask>;
  updateMaintenanceTask(id: string, task: Partial<InsertMaintenanceTask>): Promise<MaintenanceTask | undefined>;
  deleteMaintenanceTask(id: string): Promise<boolean>;

  // Alerts
  getAlerts(): Promise<Alert[]>;
  getAlert(id: string): Promise<Alert | undefined>;
  getActiveAlerts(): Promise<Alert[]>;
  getAlertsByAsset(assetId: string): Promise<Alert[]>;
  createAlert(alert: InsertAlert): Promise<Alert>;
  updateAlert(id: string, alert: Partial<InsertAlert>): Promise<Alert | undefined>;
  deleteAlert(id: string): Promise<boolean>;

  // Performance Metrics
  getPerformanceMetrics(assetId: string, limit?: number): Promise<PerformanceMetric[]>;
  createPerformanceMetric(metric: InsertPerformanceMetric): Promise<PerformanceMetric>;

  // Dashboard Analytics
  getDashboardMetrics(): Promise<{
    totalAssets: number;
    activeMaintenance: number;
    uptimeRate: number;
    criticalAlerts: number;
  }>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private assets: Map<string, Asset>;
  private maintenanceTasks: Map<string, MaintenanceTask>;
  private alerts: Map<string, Alert>;
  private performanceMetrics: Map<string, PerformanceMetric>;

  constructor() {
    this.users = new Map();
    this.assets = new Map();
    this.maintenanceTasks = new Map();
    this.alerts = new Map();
    this.performanceMetrics = new Map();
    this.initializeData();
  }

  private initializeData() {
    // Create default user
    const defaultUser: User = {
      id: randomUUID(),
      username: "admin",
      password: "admin123",
      name: "John Smith",
      role: "Maintenance Manager",
      email: "john.smith@assetsentry.com",
      createdAt: new Date(),
    };
    this.users.set(defaultUser.id, defaultUser);

    // Create sample assets
    const sampleAssets: Asset[] = [
      {
        id: randomUUID(),
        assetId: "AS001",
        name: "Centrifugal Pump A1",
        type: "Pump System",
        status: AssetStatus.OPERATIONAL,
        location: "Building A - Floor 1",
        description: "Primary water circulation pump for cooling system",
        manufacturer: "FlowTech Industries",
        model: "FT-2500X",
        serialNumber: "FT2500X-2024-001",
        installDate: new Date("2024-01-15"),
        lastMaintenance: new Date("2024-03-15"),
        nextMaintenance: new Date("2024-06-15"),
        performance: "85.00",
        specifications: {
          power: "15kW",
          flow_rate: "250 L/min",
          pressure: "3.5 bar"
        },
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      {
        id: randomUUID(),
        assetId: "AS002",
        name: "Generator Unit B3",
        type: "Generator",
        status: AssetStatus.MAINTENANCE,
        location: "Building B - Basement",
        description: "Backup power generator for emergency situations",
        manufacturer: "PowerGen Corp",
        model: "PG-500E",
        serialNumber: "PG500E-2024-003",
        installDate: new Date("2024-02-01"),
        lastMaintenance: new Date("2024-03-10"),
        nextMaintenance: new Date("2024-04-25"),
        performance: "60.00",
        specifications: {
          power_output: "500kW",
          fuel_type: "Diesel",
          runtime: "24 hours"
        },
        createdAt: new Date(),
        updatedAt: new Date(),
      },
      {
        id: randomUUID(),
        assetId: "AS003",
        name: "HVAC System C2",
        type: "HVAC",
        status: AssetStatus.OPERATIONAL,
        location: "Building C - Rooftop",
        description: "Climate control system for office areas",
        manufacturer: "ClimateControl Systems",
        model: "CC-HVAC-1000",
        serialNumber: "CC1000-2024-007",
        installDate: new Date("2024-01-20"),
        lastMaintenance: new Date("2024-03-20"),
        nextMaintenance: new Date("2024-06-20"),
        performance: "92.00",
        specifications: {
          cooling_capacity: "100 tons",
          heating_capacity: "150kW",
          efficiency_rating: "SEER 15"
        },
        createdAt: new Date(),
        updatedAt: new Date(),
      }
    ];

    sampleAssets.forEach(asset => this.assets.set(asset.id, asset));

    // Create sample alerts
    const sampleAlerts: Alert[] = [
      {
        id: randomUUID(),
        assetId: sampleAssets[0].id,
        title: "Pump System A1 - Pressure Drop",
        description: "Critical pressure drop detected in main circulation pump",
        level: AlertLevel.CRITICAL,
        status: "active",
        triggerType: "performance",
        metadata: { pressure_reading: "2.1 bar", threshold: "3.0 bar" },
        acknowledgedBy: null,
        acknowledgedAt: null,
        resolvedAt: null,
        createdAt: new Date(Date.now() - 2 * 60 * 1000), // 2 minutes ago
      },
      {
        id: randomUUID(),
        assetId: sampleAssets[1].id,
        title: "Generator B3 - Maintenance Due",
        description: "Scheduled maintenance window approaching in 2 days",
        level: AlertLevel.WARNING,
        status: "active",
        triggerType: "maintenance",
        metadata: { due_date: "2024-04-25", days_remaining: 2 },
        acknowledgedBy: null,
        acknowledgedAt: null,
        resolvedAt: null,
        createdAt: new Date(Date.now() - 15 * 60 * 1000), // 15 minutes ago
      },
      {
        id: randomUUID(),
        assetId: sampleAssets[2].id,
        title: "HVAC System C2 - Efficiency Update",
        description: "Energy efficiency improved by 8% after recent optimization",
        level: AlertLevel.INFO,
        status: "active",
        triggerType: "performance",
        metadata: { efficiency_improvement: "8%", previous_rating: "84%", current_rating: "92%" },
        acknowledgedBy: null,
        acknowledgedAt: null,
        resolvedAt: null,
        createdAt: new Date(Date.now() - 60 * 60 * 1000), // 1 hour ago
      }
    ];

    sampleAlerts.forEach(alert => this.alerts.set(alert.id, alert));

    // Create sample maintenance tasks
    const sampleTasks: MaintenanceTask[] = [
      {
        id: randomUUID(),
        assetId: sampleAssets[1].id,
        title: "Monthly Generator Inspection",
        description: "Routine monthly inspection and maintenance of backup generator",
        type: MaintenanceType.PREVENTIVE,
        priority: Priority.MEDIUM,
        status: "scheduled",
        assignedTo: defaultUser.id,
        scheduledDate: new Date("2024-04-25"),
        completedDate: null,
        estimatedDuration: 120,
